mapper.select:
select t.batch_header_id as BATCH_HEADER_ID, t.workflow_instance_id as WORKFLOW_INSTANCE_ID, t.doc_code as DOC_CODE, t.doc_verifier as DOC_VERIFIER, DATE_FORMAT( t.doc_date,'%Y-%m-%d') as DOC_DATE, t.doc_status as DOC_STATUS, t.doc_subject as DOC_SUBJECT, t.document_id as DOCUMENT_ID, (select b.full_name from EHRBASE_EMP_BASIC b where b.person_id = t.attribute10) as ATTRIBUTE10, t.attribute9 as ATTRIBUTE9, (select b.full_name from EHRBASE_EMP_BASIC b where b.person_id = t.attribute8) as ATTRIBUTE8, t.attribute7 as ATTRIBUTE7, t.attribute6 as ATTRIBUTE6, t.attribute5 as ATTRIBUTE5, t.attribute4 as ATTRIBUTE4, (select count(1) from ehrcuc_org_header_doc d where d.batch_id = t.batch_header_id) as batCount from EHRCUC_ORG_HEADER_BATCH t Where 1=1
AND t.doc_code like concat(concat('%',#{batchCode}),'%')
AND t.doc_status = #{ workFlowStatus }
AND t.doc_verifier = #{ batchVerifier }
AND (select b.full_name from EHRBASE_EMP_BASIC b where b.person_id = t.attribute8) like concat(concat('%',#{batchVerifier}),'%')
AND date_format(t.attribute9,'%Y-%m-%d') >= date_format(#{batDateS},'%Y-%m-%d')
AND date_format(t.attribute9,'%Y-%m-%d') <= date_format(#{batDateE},'%Y-%m-%d')
AND t.create_by = #{operatorId}
order by t.doc_status,t.attribute9 DESC
